import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthLogin, AuthResponse } from 'src/app/shared/model/auth.model';
import { AuthService } from 'src/app/shared/services/auth/auth.service';

@Component({
  selector: 'spd-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  public myForm!: FormGroup;
  constructor( private readonly router: Router, private authSrv: AuthService) {}


  public ngOnInit(): void {
    this.initForm();
  }

  public initForm() {
    this.myForm = new FormGroup({
      username: new FormControl(''),
      password: new FormControl(''),

    });
  }
  public login() {
    const request: AuthLogin =  {
      username: this.myForm.value.username,
      password: this.myForm.value.password
    }

    this.authSrv.auth(request).subscribe((resp: AuthResponse) => {
      if(resp?.enter) {
        this.router.navigateByUrl('spd/dashboard');
      } else {
         console.log(resp?.msj)
      }
    });
  }

}
<div class="header">
  <h1 class="title-login">Sistema de Control de Planeaciones Docentes</h1>
</div>

<div class="background-container">
  <div class="card-container">
    <p-card [style]="{ 'width': '500px', 'height': '600px' }">
      <ng-template pTemplate="header">
        <div class="p-grid container-header">
          <div class="p-col-12">
            <p class="text-center title-header">Iniciar Sesión</p>
          </div>
        </div>
      </ng-template>
      <form [formGroup]="myForm">
      <div class="flex flex-column gap-2 mt-4">
        <label for="username">Username</label>
        <input formControlName="username" pInputText id="username" />
      </div>

      <div class="flex flex-column gap-2 mt-4">
        <label for="password">Clave de Acceso</label>
        <input formControlName="password" pInputText id="password" type="password" />
      </div>

      <div class="container-btn mt-6">
        <p-button (click)="login()" label="Aceptar" type="submit"></p-button>
      </div>
      </form>
      <ng-template pTemplate="footer">
        <div class="p-grid container-footer">
          <div class="p-col-12">
            <p class="text-center">BIENVENIDO</p>
          </div>
        </div>
      </ng-template>
    </p-card>
  </div>
</div>
